import React, {Component} from "react"
import {Link} from "react-router-dom"

import "../css/main.css"
import logo from "../images/logo.png"
import cart from "../images/cart.png"
import InstrumentTable from "./InstrumentTable.js"

import axios from "axios"


import {SERVER_HOST} from "../config/global_constants"


export default class home extends Component 
{
    constructor(props) 
    {
        super(props)
        
        this.state = {
            
        }
    }
    
    
    componentDidMount() 
    {
        axios.get(`${SERVER_HOST}`)
        .then(res => 
        {
            if(res.data)
            {
                if (res.data.errorMessage)
                {
                    console.log(res.data.errorMessage)    
                }
                else
                {           
                    console.log("Records read")   
                    this.setState({instruments: res.data})
                }   
            }
            else
            {
                console.log("Record not found")
            }
        })
    }

  
    render() {
        return (
            <div className="store-container">
 		     <nav className ="store-nav">
                    <div className = "store-left">
		            <Link className="green-button" to={"/Login"}>Login</Link>
                    <Link className="blue-button" to={"/Register"}>Register</Link>

                    <img src = {logo} alt = "logo" className = "store-logo"></img></div>

                    <h1 className = "nav-title">DJS Music Store</h1>

                    <div className ="nav-links">
                        <div className = "nav-right">
                        <Link to = "/cart" className = "nav-button">
                        <img src = {cart} className = "cart-img"></img></Link>

                        <Link to="/addProduct" className="nav-button">Add New Product</Link>

                    </div>
                </div>
            </nav>
                

                <h2>Welcome to the DJS Store</h2>
                
                    <h3>Featured Albums</h3>
                    <ul>
                        
                    </ul>

    <h3>Instruments for Sale</h3>
    {this.state.instruments && this.state.instruments.length > 0 ? (
        <InstrumentTable instruments={this.state.instruments} />
    ) : (
        <p>Loading instruments...</p>
    )}
                </div>
            
        );
    }
}
